package test2;

public class switchcase {

	public static void main(String[] args) {
		int flowers = 10;
	// TODO Auto-generated method stub
    switch (flowers)
    {
	case 1:
		System.out.println("1.lotus");
		
	case 2:
		System.out.println("2.roja");
	case 3:
		System.out.println("3.lily");
	case 4:
		System.out.println("4.jasmine");
	case 5:
		System.out.println("5.marigold");
	default:
		System.out.println("no flowers");
		
	}	}

}
